mapper.select:
SELECT COALESCE(MAX(SEQ + 1), 0)
FROM TB_RECEIPT
WHERE USER_ID       = #{userId}
  AND PAYMENT_DATE  = #{paymentDate}
;

SELECT payment_date
      ,seq
      ,payment_amount
      ,payment_location
FROM TB_RECEIPT
WHERE USER_ID = #{userId}
  AND payment_date BETWEEN #{dateStart} AND #{dateEnd}
  AND DEL_YN = 'N'
ORDER BY payment_date ${sortBy}
;

mapper.update:
UPDATE TB_RECEIPT
SET del_yn = 'Y'
WHERE user_id       = #{userId}
  AND seq           = #{seq}
  AND payment_date  = #{paymentDate}

mapper.select:
SELECT COALESCE(SUM(payment_amount::int), 0)
FROM TB_RECEIPT
WHERE user_id = #{userId}
  AND payment_date BETWEEN #{dateStart} AND #{dateEnd}
  AND del_yn = 'N'
